function scrollmobilemenu() {
	// Hide Header on on scroll down
	var didScroll;
	var lastScrollTop = 0;
	var delta = 5;
	var targetMenu = $('.navbar-default');
	var navbarHeight = targetMenu.outerHeight();

	$(window).scroll(function(event){
	    didScroll = true;
	});
	
	setInterval(function() {
	    if (didScroll) {
	        hasScrolled();
	        didScroll = false;
	    }
	}, 250);
	
	function hasScrolled() {
	    var st = $(this).scrollTop();
	    var targetMenu = $('.navbar-default');
		var header=$('.headerstage').outerHeight();
	    var nav=targetMenu.find('nav');
	    
	    // se aperto il menu mobile
	    if(nav.hasClass('in')) {
			return;
	    }
	    
	    // Make sure they scroll more than delta
	    if(Math.abs(lastScrollTop - st) <= delta){
	        return;
	    }
	    // If they scrolled down and are past the navbar, add class .nav-up.
	    // This is necessary so you never see what is "behind" the navbar.
	    //if ((st <= 0) || st > lastScrollTop && st > navbarHeight ){
		if ((st <= header) ){
	        // Scroll Down
		        targetMenu.removeClass('is-sticky');
		        targetMenu.find('nav').removeClass('in');
	    } else {
	        // Scroll Up		        
	        if((st > header) && st - $(window).height() < $(document).height()) {
            	targetMenu.addClass('is-sticky');
	        }
	    }
	    lastScrollTop = st;
	}
		
	togglemenuclass();
}


