function indicators(s, name){
	var divs = s.find(".item");
	s.find('.carousel-indicators').html('');
	for(var i = 0; i < divs.length; i++) {
		var inb='<li data-target="#'+name+'" data-slide-to="'+i+'"></li>';
		$(inb).appendTo(s.find('.carousel-indicators'));
	}
}



function groupslides(s){
	var divs = s.find("article");
	if($(window).width() >= 990) {
		for(var i = 0; i < divs.length; i+=3) {
			$(divs).removeClass('item').removeClass('active');
			if(!$(divs[i]).parent().hasClass('item-grouped')) {
				divs.slice(i, i+3).wrapAll("<div class='item item-grouped'></div>");			
			}
			else {
				divs.unwrap();
				divs.addClass('item');
			}
			s.find(".item").first().addClass('active');
		}
	}		
}



function carouselutils(){
	var c=$('.carousel');
	c.each(function(){
		var s=$(this);
		var name=s.attr('id');
		var ind=$('<ol class="carousel-indicators"></ol>');
		var ctrl=$('<a class="left carousel-control" href="#'+name+'" role="button" data-slide="prev"><span class="icon-prev"><i class="icon-arrow-back"></i></span></a><a class="right carousel-control" href="#'+name+'" role="button" data-slide="next"><span class="icon-next"><i class="icon-arrow-forward"></i></span></a>');
		if(s.hasClass('grouped')) {
			groupslides(s);
		}
		var items=s.find('.item').length;
		if(items > 1) {
			s.prepend(ind);
			s.append(ctrl);
		}
		indicators(s, name);
		//s.carousel({ interval: 6000, pause: true });
		s.find('.item').first().addClass('active');
		s.find('.carousel-indicators li:first-child').addClass('active');
		//fixcarouselicons(s);
	}); 
 
	//Enable swiping...
	$(".carousel-inner").swipe( {
		//Generic swipe handler for all directions
		swipeLeft:function(event, direction, distance, duration, fingerCount) {
			$(this).parent().carousel('next'); 
		},
		swipeRight: function() {
			$(this).parent().carousel('prev'); 
		},
		//Default is 75px, set to 0 for demo so any distance triggers swipe
		threshold:0
	});
}



